var msRegular={
  mobile:/^((1)+\d{10})$/,
  tel:/^\d{3,4}-\d{7,9}$/,//电话号码格式010-12345678
  hanzi:/^[\u4E00-\u9FFF]+$/,
  hanzipy:/^[\u4E00-\u9FFFa-zA-Z]+$/,
  numword:/^[a-zA-Z0-9]+$/,
  float2:/^(([0-9]*)|([0-9]+\.[0-9]{1,2}))$/,
  Int:/^\d*$/
}
//手机号
$.validator.addMethod("mobile", function(value, element) { 
var length = value.length; 
return this.optional(element) || (length == 11 && msRegular.mobile.test(value)); 
}, "手机号码格式错误");
//座机
jQuery.validator.addMethod("itTel", function(value, element) {
return this.optional(element) || (msRegular.tel.test(value));
}, "电话格式有误，正确为区号-号码 如010-12345678");
  //限汉字
$.validator.addMethod("hanzi", function(value, element) { 
return true;
//return this.optional(element) || (msRegular.hanzi.test(value)); 
}, "只限汉字");
  //限汉字加拼音
$.validator.addMethod("hanzipy", function(value, element) { 
return this.optional(element) || (msRegular.hanzipy.test(value)); 
}, "只限汉字或拼音");
//字母数字
$.validator.addMethod("alnum", function(value, element) {
return this.optional(element) || msRegular.numword.test(value);
}, "只能包括英文字母和数字");
//数字最多保留两位
$.validator.addMethod("floor2", function(value, element) {
return this.optional(element) || msRegular.float2.test(value);
}, "只能填数字且最多两位小数");
//数字整数
$.validator.addMethod("int", function(value, element) {
return this.optional(element) || msRegular.Int.test(value);
}, "只能填整数");
//设置文件大小
$.validator.addMethod("fileSizeM", function(value,element,param) {
    var file=element.files[0];
    if(!file){return true;}//没有文件则不判断
    var fileSize=file.size;
    var maxSize = parseInt(param)*1024*1024;
    if(fileSize <= maxSize){
        return true;
    }else{
        return false;
    }
}, "请上传大小在{0}M以下的文件"); 